sys_ux_data_broker_graphql.query:
query ($studentSysId: ID!) {
	xSncGraphTutor {
		university {
			getStudent(id: $studentSysId) {
				id
				name
				email
				phone
				city
				state
				incidents {
					id
					number
					shortDescription
				}
			}
		}
	}
}

